using E_Library.Models;
using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace E_Library.Controllers
{
    public class BooksController : Controller
    {

        List<Book> books = new List<Book>()
        {
            new Book(){ Id = 1, Title="How to program Java", Author= "Abdulmajeed Alamymuni", Price=5.99f, Description="Learn the basics of java programming through real life examples", ISBN="726472948-0", Image= "data:image/jpeg;base64,"},
            new Book(){ Id = 2, Title="C# for beginners", Author= "Saeed Alqarni",  Price=3.99f, Description="Don't know where to start in C#? This Book will guide you to the right path to becoming a pro in C#", ISBN="547862548-1", Image="data:image/jpeg;base64,"},
            new Book(){ Id = 3, Title="Practical software testing", Author= "Abdulmajeed Alamymuni", Price=9.99f, Description="Practical examples for software testing", ISBN="688715872-3", Image="data:image/jpeg;base64,"}
        };

        public IActionResult Index()
        {
            ViewData["Books"] = books;
            return View();
        }

        public IActionResult Details(int? id)
        {
            Book book = books.Find(b => b.Id == id);
            ViewBag.book = book;
            return View();
        }
    }
}
